import styled from 'styled-components'
import { Link as LinkR } from 'react-router-dom'
import { Link as LinkS } from 'react-scroll'

export const Nav = styled.nav`
    background: #000000;
    height: 80px;
    display:flex;
    justify-content: center;
    align-items: center;
    font-size: 1rem;
    position: sticky;
    top: 0;
    z-index: 10;

    @media screen and (max-width:960px){
        transition: 0.8s all ease;
    }
`
export const NavbarContainer = styled.div`
    display:flex;
    justify-content:space-between;
    height: 80px;
    z-index: 1;
    width:100%;
    padding:0 24px;
    max-width:1100px;
`
export const NavLogo = styled(LinkR)`
    color: #ee1212;
    justify-self:flex-start;
    cursor: pointer;
    font-size:1.5rem;
    display:flex;
    align-items:center;
    margin-left:24px;
    font-weight:bold;
    text-decoration:none;
`
export const MobileIcons=styled.div`
    display: none;

    @media screen and (max-width:768px){
        display:block;
        top:0;
        right:0;
        color:#fff;
        position: absolute;
        cursor: pointer;
        font-size:1.8rem;
        transform:translate(-100%,60%);
}
`
export const NavMenu = styled.ul`
    display:flex;
    align-items:center;
    list-style:none;
    margin-right:22px;
    text-align:center;

    @media screen and (max-width:768px){
        display: none;
    }
`
export const NavItem = styled.li`
    height: 80px;
`
export const NavLinks=styled(LinkS)`
    color:white;
    display:flex;
    align-items:center;
    padding:0 1rem;
    height: 100%;
    text-decoration:none;
    cursor: pointer; 

    &.active{
        border-bottom:3px solid green;
    }
    &:hover{
        /* transition:all 0.2s ease-in-out; */
        border-bottom:3px solid green;
        color:red;
    }   
`
export const NavBtn=styled.nav`
    display:flex;
    align-items:center;

    @media screen and (max-width:768px){
        display:none;
    }
`
export const NavbtnLink=styled(LinkR)`
    border-radius:50px;
    background:blue;
    white-space:nowrap;
    padding:10px 22px;
    color:orange;
    font-size:16px;
    outline:none;
    border:none;
    cursor: pointer;
    transition:all 0.2s ease-in-out;
    text-decoration:none;

    &:hover{
        transition:all 0.2s ease-in-out;
        background:purple;
        color:red;
    }
`
