import styled from 'styled-components'
import {MdKeyboardArrowRight,MdArrowForward} from 'react-icons/md'

export const HeroContainer=styled.div`
    background:#0c0c0c;
    display:flex;
    justify-content:center;
    align-items:center;
    padding:0 30px;
    height:800px;
    position: relative;
    z-index: 1;
`

export const HeroBg=styled.div`
    position: absolute;
    top:0;
    right:0;
    left:0;
    bottom:0;
    width:100%;
    height: 100%;
    overflow:hidden;
`

export const VideoBg=styled.video`
    width:100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit:cover;
    background:#232a34;
`
export const HeroContent=styled.div`
    z-index: 3;
    /* top: 174px; */
    max-width:1200px;
    position:absolute;
    padding: 8px 24px;
    display:flex;
    flex-direction:column;
    align-items:center;
`

export const Heroh1=styled.h1`
    color:#fff;
    font-size:40px;
    text-align:center;
    /* font-family: 'Noto Sans JP', sans-serif; */
    /* font-family: 'Verdana'; */

    @media screen and (max-width:768px){
        font-size:40px;
    }

    @media screen and (max-width:400px){
        font-size:32px;
    }
`

export const HeroP=styled.p`
    margin-top:24px;
    color:#fff;
    font-size:24px;
    text-align:center;
    max-width:600px;
    /* font-family: 'Noto Sans JP', sans-serif; */

    @media screen and (max-width:768px){
        font-size:24px;
    }

    @media screen and (max-width:400px){
        font-size:18px;
    }
`
export const HerobtnWrapper=styled.div`
    margin-top:32px;
    display:flex;
    flex-direction: column;
    align-items: center;
`

export const ArrowForward=styled(MdArrowForward)`
    margin-left: 8px;
    font-size: 20px;
`

export const ArrowRight=styled(MdKeyboardArrowRight)`
    margin-left: 8px;
    font-size: 20px;
`
